.nav li {
    display: block;
    float: left;
    height: 64px;
    margin-right: 1px;
    position: relative;
}

.nav li a {
    display: block;
    height: 100%;
    font: 16px/64px "OpenSansLight", sans-serif;
    color: rgb(255, 255, 255);
    padding: 0px 24px;
    text-decoration: none;
    transition: background 0.3s ease 0s;
}

.nav li a:hover {
    background: rgb(0, 165, 242) none repeat scroll 0% 0%;
}

.nav li {
    perspective: 1000px;
}

.nav li a {
    position: relative;
    background: rgb(0, 149, 218) none repeat scroll 0% 0%;
    transition: transform 0.3s ease 0s;
    transform-origin: 50% 0px 0px;
    transform-style: preserve-3d;
}

.nav li:first-child::after,
.nav__item_first::after {
    display: block;
    content: " ";
    position: absolute;
    z-index: 200;
    bottom: 0px;
    left: 0px;
    width: 40px;
    height: 26px;
}

.nav li a::before {
    display: block;
    position: absolute;
    top: 100%;
    left: 0px;
    width: 100%;
    height: 100%;
    text-align: center;
    background: rgb(9, 101, 160) none repeat scroll 0% 0%;
    content: attr(data-hover);
    transition: background 0.3s ease 0s;
    transform: rotateX(-90deg);
    transform-origin: 50% 0px 0px;
}

.nav li:hover a,
.nav li:focus a {
    transform: rotateX(90deg) translateY(-22px);
}

.nav li:hover a::before,
.nav li:focus a::before {
    background: rgb(36, 131, 197) none repeat scroll 0% 0%;
}

